function getWebhooksTextArea(){return document.getElementById("webhooks")}function getSaveStatus(){return document.getElementById("save-status")}function getSaveButton(){return document.getElementById("save")}function save_options(){const a=getWebhooksTextArea();try{JSON.parse(a.value)}catch(a){const b=getSaveStatus();return void(b.textContent="Invalid json.")}chrome.storage.sync.set({webhooks:a.value},()=>{chrome.runtime.reload();const a=getSaveStatus();a.textContent="Options saved.",setTimeout(()=>{a.textContent="",window.close()},750)})}function restore_options(){chrome.storage.sync.get({webhooks:"[]"},a=>{const b=getWebhooksTextArea();b.value=a.webhooks})}document.addEventListener("DOMContentLoaded",restore_options);const saveButton=getSaveButton();saveButton.addEventListener("click",save_options);